Rules.InvariantScript:
"https://quickchart.io/chart?c=" & EncodeUrl(
    "
    {
  'type': 'bar',
  'data': {
    'labels': [
      "&Concat(Distinct(char_line_2_v01.Item_Chart,ChartLabel),"'"&Value&"',")&"
    ],
    'datasets': [
      {
        'type': 'line',
        'label': 'Dataset 1',
        'borderColor': 'rgb(54, 162, 235)',
        'borderWidth': 2,
        'fill': true,
        'data': [
          "&Concat(Filter(char_line_2_v01.Item_Chart,ChartItem = "ItemA"),"'"&ChartValue&"',")&"
        ]
      },
      {
        'type': 'line',
        'label': 'Dataset 2',
        'backgroundColor': 'rgb(255, 99, 132)',
        'data': [
          "&Concat(Filter(char_line_2_v01.Item_Chart,ChartItem = "ItemB"),"'"&ChartValue&"',")&"
        ],
        'borderColor': 'white',
        'borderWidth': 2
      },
      {
        'type': 'bar',
        'label': 'Dataset 3',
        'backgroundColor': 'rgb(75, 192, 192)',
        'data': [
          "&Concat(Filter(char_line_2_v01.Item_Chart,ChartItem = "ItemC"),"'"&ChartValue&"',")&"
        ]
      }
    ]
  },
  'options': {
    'responsive': true,
    'title': {
      'display': true,
      'text': 'Chart.js Combo Bar Line Chart'
    },
    'color': ['#ffffff'],
    'tooltips': {
      'mode': 'index',
      'intersect': true
    }
  }
}
    "
)


