Rules.InvariantScript:
"https://quickchart.io/chart?c="&EncodeUrl("{
  'type': 'outlabeledPie',
  'data': {

    'labels': ["&Concat(chart_donut.Items_Chart,"'"&ChartItem&"',")&"],
    'datasets': [{
        'backgroundColor': 
                        ["&
                         If( chart_donut.IsTrue_DarkMode ,
                            Concat(chart_donut.Color_DarkMode,"'"&Color&"',"), 
                            Concat(chart_donut.Color_LightMode,"'"&Color&"',")
                         )
                        &"]
                        ,
        'data': ["&Concat(chart_donut.Items_Chart,"'"&ChartValue&"',")&"] , 
        'borderColor' : ['#000000'] , 
        'borderWidth' : 1
    }]
  },
  'options': {
    'plugins': {
      'legend': false,
      'outlabels': {
        'text': '%l',
        'color': ["& 
        If( chart_donut.IsTrue_DarkMode ,
                            Concat(chart_donut.Color_Font_DarkMode,"'"&Color&"',"), 
                            Concat(chart_donut.Color_Font_LightMode,"'"&Color&"',")
                         )
        &"],
        'stretch': 20 ,
        'backgroundColor' : false
        ,
        'font': {

          'resizable': true,
          'minSize': " & chart_donut.Size_Font*1.8 & " ,
          'maxSize': " & chart_donut.Size_Font*1.2 & " ,
          'fontFamily' : 'Noto Sans'
        }
      }
    }
  }
}
")
